    private void btnAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAddActionPerformed
      enableSaveCancel(true);
        clearFields();
        // Set navigation to current count for new record
        recNav = recCount;
    }//GEN-LAST:event_btnAddActionPerformed

    private void btnPreviousActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnPreviousActionPerformed
      if (recNav > 0) {
            recNav--;
            viewInstruct(recNav);
        } else {
            JOptionPane.showMessageDialog(this, "This is the first record", "Info", JOptionPane.INFORMATION_MESSAGE);
        }             
    }//GEN-LAST:event_btnPreviousActionPerformed

    private void btnSaveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSaveActionPerformed
   if (validateFields()) {
            enableSaveCancel(false);
            storageInstruct(recNav);
            // Only increment recCount if we're adding a new record
            if (recNav == recCount) {
                recCount++;
            }
            JOptionPane.showMessageDialog(this, "Record Saved", "Success", JOptionPane.INFORMATION_MESSAGE);
    }//GEN-LAST:event_btnSaveActionPerformed
    }
    private void btnEditActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEditActionPerformed
        enableSaveCancel(true);
        if (recCount > 0) {
            enableSaveCancel(true);
        } else {
            JOptionPane.showMessageDialog(this, "No records to edit", "Info", JOptionPane.INFORMATION_MESSAGE);
        }
    }//GEN-LAST:event_btnEditActionPerformed

    private void btnCancelActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCancelActionPerformed
         enableSaveCancel(false);
        if (recCount > 0) {
            viewInstruct(recNav);
        } else {
            clearFields();
        }                
    }//GEN-LAST:event_btnCancelActionPerformed

    private void btnViewActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnViewActionPerformed
        if (recCount > 0) {
            enableEdit(true);
            recNav = 0;
            viewInstruct(recNav);
        } else {
            JOptionPane.showMessageDialog(this, "No records to view", "Info", JOptionPane.INFORMATION_MESSAGE);
        }             
    }//GEN-LAST:event_btnViewActionPerformed

    private void btnFirstActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnFirstActionPerformed
        if (recCount > 0) {
            recNav = 0;
            viewInstruct(recNav);                       
    }//GEN-LAST:event_btnFirstActionPerformed
    }
    private void btnEndviewActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEndviewActionPerformed
         enableEdit(false);          
    }//GEN-LAST:event_btnEndviewActionPerformed

    private void btnNextActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnNextActionPerformed
         if (recNav < recCount - 1) {
            recNav++;
            viewInstruct(recNav);
        } else {
            JOptionPane.showMessageDialog(this, "This is the last record", "Info", JOptionPane.INFORMATION_MESSAGE);
        }              
    }//GEN-LAST:event_btnNextActionPerformed

    private void btnLastActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLastActionPerformed
        if (recCount > 0) {
            recNav = recCount - 1;
            viewInstruct(recNav);
        }             
    }//GEN-LAST:event_btnLastActionPerformed

    private void btnDeleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDeleteActionPerformed
      if (recCount > 0 && recNav < recCount) {
            int response = JOptionPane.showConfirmDialog(this, 
                "Are you sure you want to delete this record?", "Confirm Delete", 
                JOptionPane.YES_NO_OPTION);
            
            if (response == JOptionPane.YES_OPTION) {
                for (int i = recNav; i < recCount - 1; i++) {
                    aName[i] = aName[i + 1];
                    aAge[i] = aAge[i + 1];
                    aAddress[i] = aAddress[i + 1];
                    aBirthday[i] = aBirthday[i + 1];
                    aEmail[i] = aEmail[i + 1];
                    aSex[i] = aSex[i + 1];
                }
                recCount--;
                
                if (recCount == 0) {
                    clearFields();
                    enableEdit(false);
                } else {
                    if (recNav >= recCount) {
                        recNav = recCount - 1;
                    }
                    viewInstruct(recNav);
                }
                JOptionPane.showMessageDialog(this, "Record Deleted", "Delete", JOptionPane.INFORMATION_MESSAGE);
            }
        } else {
            JOptionPane.showMessageDialog(this, "No records to delete", "Info", JOptionPane.INFORMATION_MESSAGE);
        }
    }//GEN-LAST:event_btnDeleteActionPerformed

