    // ADD MISSING VALIDATION METHOD
    private boolean validateFields() {
        if (txtName.getText().trim().isEmpty()) {
            JOptionPane.showMessageDialog(this, "Please enter a name", "Validation Error", JOptionPane.ERROR_MESSAGE);
            txtName.requestFocus();
            return false;
        }
        
        if (txtAge.getText().trim().isEmpty()) {
            JOptionPane.showMessageDialog(this, "Please enter age", "Validation Error", JOptionPane.ERROR_MESSAGE);
            txtAge.requestFocus();
            return false;
        }
        
        try {
            int age = Integer.parseInt(txtAge.getText().trim());
            if (age <= 0 || age > 150) {
                JOptionPane.showMessageDialog(this, "Please enter a valid age (1-150)", "Validation Error", JOptionPane.ERROR_MESSAGE);
                txtAge.requestFocus();
                return false;
            }
        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(this, "Age must be a number", "Validation Error", JOptionPane.ERROR_MESSAGE);
            txtAge.requestFocus();
            return false;
        }
        
        if (txtSex.getText().trim().isEmpty()) {
            JOptionPane.showMessageDialog(this, "Please enter sex", "Validation Error", JOptionPane.ERROR_MESSAGE);
            txtSex.requestFocus();
            return false;
        }
        
        return true;
    }
    
    public void enableSaveCancel(boolean enable) {
        btnSave.setEnabled(enable);
        btnCancel.setEnabled(enable);
        btnBrowse.setEnabled(enable);
        
        btnAdd.setEnabled(!enable);
        btnEdit.setEnabled(!enable);
        
        txtName.setEnabled(enable);
        txtAge.setEnabled(enable);
        txtAddress.setEnabled(enable);
        txtBirthday.setEnabled(enable);
        txtEmail.setEnabled(enable);
        txtSex.setEnabled(enable);
    }

   public void enableEdit(boolean enable) {
        btnView.setEnabled(!enable);
        btnAdd.setEnabled(!enable);
        
        btnEndview.setEnabled(enable);
        btnNext.setEnabled(enable);
        btnPrevious.setEnabled(enable);
        btnFirst.setEnabled(enable);
        btnLast.setEnabled(enable);
        btnDelete.setEnabled(enable);
        btnEdit.setEnabled(!enable);
    }
 public void storageInstruct(int index) {
    aName[index] = txtName.getText();
    aAge[index] = txtAge.getText();
    aBirthday[index] = txtBirthday.getText();
    aEmail[index] = txtEmail.getText();
    aAddress[index] = txtAddress.getText();
    aSex[index] = txtAddress.getText();
}

public void viewInstruct(int viewerIndex) {
    if (viewerIndex >= 0 && viewerIndex < recCount) {
        txtName.setText(aName[viewerIndex]);
        txtAge.setText(aAge[viewerIndex]);
        txtAddress.setText(aAddress[viewerIndex]);
        txtBirthday.setText(aBirthday[viewerIndex]);
        txtEmail.setText(aEmail[viewerIndex]);
        txtSex.setText(aAge[viewerIndex]);
    }
}

public void clearFields() {
    txtName.setText("");
    txtAge.setText("");
    txtAddress.setText("");
    txtBirthday.setText("");
    txtEmail.setText("");
    txtSex.setText("");
}


